import { useState, useEffect } from "react";
import ValueBoxes from "./ValueBoxes";
import InputRow from "./InputRow";
import DisplayRow from "./DisplayRow";

const MasterMindGame = () => {
    const [randomNums, setRandomNums] = useState([]);
    const [rows, setRows] = useState([]);
    const [rowsNum, setRowsNum] = useState(0);

    useEffect(() => {
        // //1. retrieve random numbers from api
        fetch("https://www.random.org/integers/?num=4&min=0&max=7&col=1&base=10&format=plain&rnd=new")
        .then((res) => {
            return res.text()
        })
        .then((data) => {
            console.log(data);
            var array = data.split("\n")
            array.pop()
            array.map((num) => {
                return parseInt(num)
            })
            setRandomNums(array);
        })
        .catch(error => {
            console.log(error)
        })
    }, [])

    const checkRow = (event) => {
        event.preventDefault();
        var rowArray = [...event.target].map((input) => { return input.value });
        rowArray.pop()
        //console.log(rowArray)
        var inputSet = new Set(rowArray);
        var randomSet = new Set(randomNums);
        var feedback = [];

        //1. check if numbers are correct and aligned
        for (let i = 0; i < rowArray.length; i++) {
            if (rowArray[i] == randomNums[i]) {
                feedback.push(2);
                deleteNumFromSets(rowArray[1]); 
            }
        }

        //2. check if any remaining inputs nums are in the random set
        for (const num in inputSet) {
            if (randomSet.has(num)) {
                feedback.push(1);
                deleteNumFromSets(num);
            }
        }

        for (let i = feedback.length; i < 4; i++) {
            feedback.push(0)
        }

        console.log("feedback" + feedback)
        //increment row number 
        setRows((prevCount) => {
            return prevCount + 1;
        })

        //2. Update the grid
        setRows([...rows, { row: rowsNum, nums: rowArray, check: feedback.sort().reverse()}])

        const deleteNumFromSets = (num) => {
            randomSet.delete(num);
            inputSet.delete(num)
        }
    }

    return <section>
        {rows.map((row) => {
            console.log(row.nums);
            return( 
            <DisplayRow key={row.row} values={row.nums} feedback={row.check}/>
            )
        })}
        <InputRow rowId="1" checkRow={checkRow} />
        </section>

}

export default MasterMindGame;
