import { useState, useEffect } from "react";
import MasterMindInputRow from "./MasterMindRow";

const MasterMindGame = () => {
    const [randomNums, setRandomNums] = useState([]);
    const [rows, setRows] = useState([]);

    useEffect(() => {
        // //1. retrieve random numbers from api
        fetch("https://www.random.org/integers/?num=4&min=0&max=7&col=1&base=10&format=plain&rnd=new")
        .then((res) => {
            return res.text()
        })
        .then((data) => {
            console.log(data);
            var array = data.split("\n")
            array.pop()
            array.map((num) => {
                return parseInt(num)
            })
            setRandomNums(array);
        })
        .catch(error => {
            console.log(error)
        })
    }, [])

    const checkRow = (event) => {
        event.preventDefault();
        let rowArray = event.target[0].value;
        console.log("rowArray" + rowArray);
        var numSet = new Set(randomNums);
        var feedback = [];
        for (let i = 0; i < rowArray.length; i++) {
            if (rowArray[i] == randomNums[i]) {
                feedback.push(2)
            } else if (numSet.has(rowArray[i])) {
                feedback.push(1)
                numSet.delete(rowArray[i])
            } else {
                feedback.push(0)
            }
        }
        console.log("feedback" + feedback)
        //1. Update the grid
        setRows([...rows, { nums: rowArray, check: feedback.sort().reverse()}])
    }

    return <section>
        <MasterMindInputRow rowId="1" checkRow={checkRow} />
        {/* {rows.map((row) => {
            return 


        })} */}
        </section>

}

export default MasterMindGame;
