import { useState, useEffect } from "react";
import classes from "./MastermindGame.module.css";
import InputRow from "./InputRow";
import DisplayRow from "./DisplayRow";

const MasterMindGame = () => {
  const [randomNums, setRandomNums] = useState([3, 3, 4, 7]);
  const [rows, setRows] = useState([]);
  const [rowsNum, setRowsNum] = useState(0);
  const [didWin, setDidWin] = useState(false);

//   useEffect(() => {
//     // //1. retrieve random numbers from api
//     fetch(
//       "https://www.random.org/integers/?num=4&min=0&max=7&col=1&base=10&format=plain&rnd=new"
//     )
//       .then((res) => {
//         return res.text();
//       })
//       .then((data) => {
//         console.log(data);
//         var array = data.split("\n");
//         array.pop();
//         array.map((num) => {
//           return parseInt(num);
//         });
//         setRandomNums(array);
//       })
//       .catch((error) => {
//         console.log(error);
//       });
//   }, []);

  const checkRow = (event) => {
    event.preventDefault();
    console.log("event target: " + event.target[0].value)
    var rowArray = [...event.target].map((input) => {
      return input.value
    });
    rowArray.pop();
    //console.log(rowArray)
    var inputArr = [...rowArray];
    var randomArr = [...randomNums];

    var feedback = [];
    console.log("input set" + inputArr);
    console.log("randomArr" + randomArr);

    const deleteNumFromSets = (num) => {
        console.log("delete num " + num);
        let a = randomArr.indexOf(num);
        let b = inputArr.indexOf(num);
        console.log("a" + a);
        console.log("b" + b);

      randomArr.splice(a, 1);
      inputArr.splice(b, 1);
    };

    //1. check if numbers are correct and aligned
    for (let i = 0; i < rowArray.length; i++) {
      if (inputArr[i] == randomArr[i]) {
        feedback.push(2);
        deleteNumFromSets(inputArr[i]);
      }
    }
    console.log("input set" + inputArr);
    console.log("randomArr" + randomArr);
    console.log("feedback after 1: " + feedback);
    //2. check if any remaining inputs nums are in the random set
    inputArr.forEach((num) => {
      if (randomArr.includes(num)) {
        feedback.push(1);
        deleteNumFromSets(num);
      }
    });

    console.log("feedback after 2: " + feedback);
    for (let i = feedback.length; i < 4; i++) {
      feedback.push(0);
    }
    //check if input is correct
    if (
      feedback.every((num) => {
        return num == 2;
      })
    ) {
      setDidWin(true);
    }
    //increment row number
    setRowsNum((prevCount) => {
      return prevCount + 1;
    });

    //2. Update the grid
    setRows([
      ...rows,
      { row: rowsNum, nums: rowArray, check: feedback.sort().reverse() },
    ]);
  };

  return (
    <section>
      <h2>MasterMind Game</h2>
      <div className={classes.game}>
        <div className={classes.guessGrid}>
          {rows.map((row) => {
            console.log(row.nums);
            return (
              <DisplayRow
                key={row.row}
                values={row.nums}
                feedback={row.check}
              />
            );
          })}
        </div>
        <InputRow rowId="1" checkRow={checkRow} />
        {didWin && <p>you won!</p>}
      </div>
    </section>
  );
};

export default MasterMindGame;
