import classes from "./DisplayRow.module.css";
import ValueBoxes from "./ValueBoxes";

const DisplayRow = (props) => {
    console.log("feedback in display rw " + props.feedback);
  var colors = props.feedback.map((num) => {
    switch (num) {
      case 0:
        return "";
      case 1:
        return "grey";
      case 2:
        return "red";
      default:
        return "";
    }
  });

  console.log("colors" + colors);
  return (
    <div className={classes.block}>
      <div className={classes.displayRow}>
        <ValueBoxes readOnly={true} values={props.values} />
      </div>
      <div className={classes.displayRow}>
        <span className={`${classes.dots} ${classes[colors[0]]}`}></span>
        <span className={`${classes.dots} ${classes[colors[1]]}`}></span>
        <span className={`${classes.dots} ${classes[colors[2]]}`}></span>
        <span className={`${classes.dots} ${classes[colors[3]]}`}></span>
      </div>
    </div>
  );
};

export default DisplayRow;
